import React, { useState } from "react";
import ExpenseItem from "./ExpenseItem";
import Card from "../UI/Card.js";
import ExpenseFilter from "./ExpensesFilter";
import "./Expenses.css";

const Expenses = (props) => {
  const [selectedYear, changeSelectedYear] = useState("");

  const selectYear = (year) => {
    console.log(selectedYear);
    changeSelectedYear(year);
  };
  return (
    <Card className="expenses">
      <ExpenseFilter onSelectYear={selectYear} />
      {props.items.map((item) => {
        // console.log(item.date.getFullYear() + " " + selectedYear);
        if (item.date.getFullYear() === selectYear) return <ExpenseItem title={item.title} amount={item.amount} date={item.date} />;
      })}
    </Card>
  );
};

export default Expenses;

