import React, { useState } from "react";

import "./ExpenseForm.css";

const ExpenseForm = (props) => {
  const [enteredTitle, setEnteredTitle] = useState("");
  const [enteredAmount, setEnteredAmount] = useState("");
  const [enteredDate, setEnteredDate] = useState("");
  const [form, updateForm] = useState(
    <form onSubmit={submitHandler}>
      <button type="sumbit" id="addExpense">
        Add New Expense
      </button>
    </form>
  );

  // const [userInput, setUserInput] = useState({
  //   enteredTitle: "",
  //   enteredAmount: "",
  //   enteredDate: "",
  // });

  // const titleChangeHandler = (event) => {
  //   setEnteredTitle(event.target.value);
  // setUserInput({
  //   ...userInput,
  //   enteredTitle: event.target.value,
  // });
  // setUserInput((prevState) => {
  //   return {
  //     ...prevState,
  //     enteredTitle: event.target.value,
  //   };
  // });
  // };

  // const amountChangeHandler = (event) => {
  //   setEnteredAmount(event.target.value);
  // setUserInput({
  //   ...userInput,
  //   enteredAmount: event.target.value,
  // });
  // setUserInput((prevState) => {
  //   return {
  //     ...prevState,
  //     enteredAmount: event.target.value,
  //   };
  // });
  // };

  // const dateChangeHandler = (event) => {
  //   setEnteredDate(event.target.value);
  // setUserInput({
  //   ...userInput,
  //   enteredDate: event.target.value,
  // });
  // setUserInput((prevState) => {
  //   return {
  //     ...prevState,
  //     enteredDate: event.target.value,
  //   };
  // });
  // };

  const inputChangeHandler = (identifier, value) => {
    if (identifier === "title") setEnteredTitle(value);
    else if (identifier === "date") setEnteredDate(value);
    else setEnteredAmount(value);
  };

  const cancelAddExpenseHandler = () => {
    updateForm(
      <button type="sumbit" id="addExpense">
        Add New Expense
      </button>
    );
  };

  const submitHandler = (event) => {
    event.preventDefault();

    if (event.target.children[0].id === "addExpense") {
      updateForm(
        <div>
          <div className="new-expense__controls">
            <div className="new-expense__control">
              <label>Title</label>
              <input type="text" value={enteredTitle} onChange={(event) => inputChangeHandler("title", event.target.value)} />
            </div>
            <div className="new-expense__control">
              <label>Amount</label>
              <input type="number" value={enteredAmount} min="0.01" step="0.01" onChange={(event) => inputChangeHandler("amount", event.target.value)} />
            </div>
            <div className="new-expense__control">
              <label>Date</label>
              <input type="Date" value={enteredDate} min="2020-06-15" max="2022-06-15" onChange={(event) => inputChangeHandler("date", event.target.value)} />
            </div>
          </div>
          <div className="new-expense__actions">
            <button onClick={cancelAddExpenseHandler}>Cancel</button>
            <button type="submit">Add Expense</button>
          </div>
        </div>
      );
    } else {
      const expenseData = {
        title: enteredTitle,
        amount: enteredAmount,
        date: new Date(enteredDate),
      };

      props.onSaveExpenseData(expenseData);

      setEnteredTitle("");
      setEnteredAmount("");
      setEnteredDate("");
    }
  };

  return { form };
};

export default ExpenseForm;
