import React, { useState } from "react";
import ExpenseItem from "./ExpenseItem";
import Card from "../UI/Card.js";
import ExpenseFilter from "./ExpensesFilter";
import "./Expenses.css";

const Expenses = (props) => {
  const [selectedYear, changeSelectedYear] = useState("2020");

  const selectYear = (year) => {
    changeSelectedYear(year);
  };

  const filteredExpenses = props.items.filter((expense) => {
    return expense.date.getFullYear().toString() === selectedYear;
  });

  let expenseContent = <p>No expenses found.</p>;

  return (
    <Card className="expenses">
      <ExpenseFilter onSelectYear={selectYear} />
      {filteredExpenses.length === 0 ? { expenseContent } : filteredExpenses.map((expense) => <ExpenseItem title={expense.title} amount={expense.amount} date={expense.date} />)}
    </Card>
  );
};

export default Expenses;
